import { useState, useEffect, useRef } from "react";
import {useDispatch} from 'react-redux'
function Body() {
  const fnameInputRef = useRef();
  const dispatch = useDispatch();

  const [fnameInp, setFNameInp] = useState("");
  const [lnameInp, setLNameInp] = useState("");

  useEffect(() => {
    fnameInputRef && fnameInputRef.current.focus();
  },[])

  return (
    <>
    <div className="modal-body">
      <div className="modal-body-row">
        <span>First Name : </span>
        <input
          ref = {fnameInputRef}
          type="text"
          value={fnameInp}
          onChange={(e) => setFNameInp(e.target.value)}
        ></input>
      </div>
      <div className="modal-body-row">
        <span>Last Name :</span>
        <input
          type="text"
          value={lnameInp}
          onChange={(e) => setLNameInp(e.target.value)}
        ></input>
      </div>
    </div>
    <div className="modal-footer">
    <button type="submit" onClick={() => dispatch(closeModal())}>OK</button>
    <button type="button" onClick={() => dispatch(closeModal())}>Cancel</button>
  </div>
  </>
  );
}

export default Body;
