import {
  ADD_ROW,
  DELETE_ROW,
  OPEN_MODAL,
  CLOSE_MODAL,
  EDIT_ROW,
} from "./constants";

const initialState = {
  modal: {
    openModal: false,
    editId: false,
    fname: "",
    lname: "",
  },
  table: [
    { id: 1, fname: "Sahadev", lname: "Tandur" },
    { id: 2, fname: "Manjunath", lname: "KL" },
  ],
};
const tableReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_ROW:
      if (!action.payload.fname && !action.payload.lname) {
        return {
          ...state,
          modal: { openModal: false, editId: false, fname: "", lname: "" },
        };
      }
      return {
        ...state,
        modal: { openModal: false, editId: false, fname: "", lname: "" },
        table: [
          ...state.table,
          {
            id: action.payload.id,
            fname: action.payload.fname,
            lname: action.payload.lname,
          },
        ],
      };
    case DELETE_ROW:
      return {
        ...state,
        table: state.table.filter((val) => val.id !== action.payload),
      };
    case EDIT_ROW:
      if (!action.payload.fname && !action.payload.lname) {
        return {
          ...state,
          modal: { openModal: false, editId: false, fname: "", lname: "" },
        };
      }
      return {
        ...state,
        table: state.table.map((val) => {
          if (val.id == action.payload.id) {
            return {
              id: action.payload.id,
              fname: action.payload.fname,
              lname: action.payload.lname,
            };
          }
          return val;
        }),
        modal: {
          openModal: false,
          editId: false,
          fname: "",
          lname: "",
        },
      };
    case OPEN_MODAL:
      return {
        ...state,
        modal: {
          ...state.modal,
          openModal: true,
          editId: action.payload.editId,
          fname: action.payload.fname,
          lname: action.payload.lname,
        },
      };
    case CLOSE_MODAL:
      return {
        ...state,
        modal: { ...state.modal, openModal: false },
      };
    default:
      return state;
  }
};

export default tableReducer;
