import { ADD_ROW } from "./constants";
// const initialState = [
//     { id: 1, name: "Sahadev", age: 28 },
//   { id: 2, name: "Tandur", age: 27 }
// ];

const initialState = {
  table: [
    { id: 1, name: "Sahadev", age: 28 },
    { id: 2, name: "Tandur", age: 27 },
  ],
};
const tableReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_ROW:
      return state.table.push(action.payload);
    case "increment":
      return {
        ...state,
        counter: {
          ...state.counter,
          count: state.counter.count + action.payload,
        },
      };
    default:
      return state;
  }
};

export default tableReducer;
