import { ADD_ROW, DELETE_ROW, EDIT_ROW, OPEN_MODAL, CLOSE_MODAL } from "./constants"

export const addRow = (name) =>{
    return {
        type : ADD_ROW,
        payload : name
    }
}

export const deletRow = (id=0) =>{
    return {
        type : DELETE_ROW,
        payload : id
    }
}

export const editRow = (id=0) =>{
    return {
        type : EDIT_ROW,
        payload : id
    }
}

export const openModal = () =>{
    return {
        type : OPEN_MODAL
    }
}

export const closeModal = () =>{
    return {
        type : CLOSE_MODAL
    }
}
