import {
  ADD_ROW,
  DELETE_ROW,
  OPEN_MODAL,
  CLOSE_MODAL,
  EDIT_ROW,
} from "./constants";
// import useId from "../../hook/useId";
// const initialState = [
//     { id: 1, name: "Sahadev", age: 28 },
//   { id: 2, name: "Tandur", age: 27 }
// ];

const initialState = {
  modal: {
    openModal: false,
    editId:false,
    fname: "",
    lname: "",
  },
  table: [
    { id: 1, fname: "Sahadev", lname: "Tandur" },
    { id: 2, fname: "Manjunath", lname: "KL" },
  ],
  //   editCell : {
  //     id: 0,
  //     fname:"",
  //     lname:""
  //   }
};
const tableReducer = (state = initialState, action) => {
  // const [currentId, getUniqueId] = useId(state.table);
  switch (action.type) {
    case ADD_ROW:
      return {
        ...state,
        modal: { openModal: false, fname: "", lname: "" },
        table: [
          ...state.table,
          {
            id: action.payload.id,
            fname: action.payload.fname,
            lname: action.payload.lname,
          },
        ],
      };
    case DELETE_ROW:
      return {
        ...state,
        table: state.table.filter((val) => val.id !== action.payload),
      };
    case EDIT_ROW:
      return {
        ...state,
        table : state.table.map(val => {
            if(val.id == action.payload.id){
                return {
                    id: val.id,
                    fname: action.payload.fname,
                    lname: action.payload.lname
                }
                return val;
            }
        })
      };
    case OPEN_MODAL:
      return {
        ...state,
        modal: {
          ...state.modal,
          openModal: true,
          editId:action.payload.editId,
          fname: action.payload.fname,
          lname: action.payload.lname,
        },
      };
    case CLOSE_MODAL:
      return {
        ...state,
        modal: { ...state.modal, openModal: false },
      };
    default:
      return state;
  }
};

export default tableReducer;
