import { ADD_ROW, DELETE_ROW, OPEN_MODAL, CLOSE_MODAL } from "./constants";
// const initialState = [
//     { id: 1, name: "Sahadev", age: 28 },
//   { id: 2, name: "Tandur", age: 27 }
// ];

const initialState = {
  openModal: false,
  table: [
    { id: 1, name: "Sahadev", age: 28 },
    { id: 2, name: "Tandur", age: 27 },
  ],
};
const tableReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_ROW:
      return state.table.push(action.payload);
    case DELETE_ROW:
      return {
        ...state,
        table: state.table.filter((val) => val.id !== action.payload),
      };
    case OPEN_MODAL:
        return {
            ...state,
            openModal : true
        }
        case OPEN_MODAL:
            return {
                ...state,
                openModal : false
            }
    default:
      return state;
  }
};

export default tableReducer;
