import { ADD_ROW } from "./constants";
// const initialState = [
//     { id: 1, name: "Sahadev", age: 28 },
//   { id: 2, name: "Tandur", age: 27 }
// ];

const initialState = {
    count : 10
}
const tableReducer = (state = initialState, action) => {
    switch(action.type){
        case ADD_ROW : return state.push(action.payload)
        case 'increment' : return state.count+1
        default : return state
    }
}

export default tableReducer
